mapper.select:
select tNo, tTitle, tWriter, tDate, tContents
from testboard.board
where tNo = #{tNo}

select count(*) 
from testboard.board 
where tNo = #{tNo}
and tPassword = #{tPassword}

mapper.update:
update testboard.board
set tTitle=#{tTitle},
tContents=#{tContents}
where tNo = #{tNo};

mapper.select:
select @rownum := @rownum +1 as no , board.*
from (
	select tNo, tTitle, tWriter, tDate, @rownum:= #{somePage}
	from testboard.board
	order by tNo desc 
	limit #{somePage}, #{cntBulletin}
	) as board
